import matplotlib.pyplot as plt

from utils.otherUtils import IO

def plot_history(dir):
	'''将训练历史Plot出来'''
	history = IO.loadJson(dir)
	x = list(range(len(history["train_loss"])))
	for item in history:
		if "loss" not in item and "acc" not in item:
			plt.plot(x,history[item],'o-',label = item)
	plt.xlabel("epoch")
	plt.title("Training History")
	plt.grid()
	plt.legend()
	plt.show()
def colored(string, fg="white", bg="black", style=0):
	fg_map = {
		"black": 30,
		"red": 31,
		"green": 32,
		"yellow": 33,
		"blue": 34,
		"purple_red": 35,
		"cyan": 36,
		"white": 37,
	}
	bg_map = dict([color, fg_map[color] + 10] for color in fg_map)
	bg_map[""] = ""

	'''
		style           意义
	-------------------------
			0        终端默认设置
			1       高亮显示
			4       使用下划线
			5       闪烁
			7       反白显示
			8       不可见
	'''
	return '\033[{};{};{}m{}\033[0m'.format(style, fg_map[fg], bg_map[bg], string)

def colorful_text(tokens,labels,colormap):
	output = ""
	for idx,token in enumerate(tokens):
		label = labels[idx]
		color = colormap[label]
		if label != "O":
			style = 1
		else:
			style = 0
		output += colored(tokens[idx],fg = color,bg="",style = style)
	return output
